import React, { Fragment ,useState, useEffect,useRef} from 'react';
import axios from 'axios';
 
import * as $ from 'jquery'
import DeleteModal from '../components/DeleteModal';

const ViewCompany=()=>{
  
    let [input,handleInput]=useState({type:'',name:'',phone:'',email:'',city:'',address:''});  
   
    const [updatedata,handleInputUpdate]=useState({id:'',name:''});
  
    let [successhide1,handleSuccessHide1]=useState('d-none');
    let [errors,handleValidation]=useState({name:''});
    let [data1,showdata]=useState([]);
    let [editmodalhide,changeModal]=useState({display:'none'});
    let [deleteid,changedeleteid]=useState('');
    const inputEl = useRef(null);
   const closeDelete=useRef(null);
    // FOr GET TABLE DATA //
   useEffect(()=>{
    
    axios.get('http://localhost:3010/company/').then((res)=>{
        
        $('#example1').DataTable().destroy();
   showdata(res.data)
   $('#example1').DataTable({
    dom: 'Bfrtip',
    buttons: [
     'copy', 'excel', 'pdf','csv','print'
 ], "paging": true,
 "pageLength": 20,
   "lengthChange": false,
   "searching": true,
   "ordering": false,
   "info": true,
   "autoWidth": false,
  });
}) 
 
},[successhide1]);
// FOR HAndle Insert Inputs //
    let handleChange=(e)=>{
       console.log(e.target.id);
       let l=e.target.id;
      handleInput({...data1,l:e.target.value})
      
    }

     
// FOR UPDATE DATA //
   const handleSubmitUpdate=(e)=>{ 
 
axios.post('http://localhost:3010/designation/update',updatedata).then(res=>{
   inputEl.current.click();
 handleSuccessHide1('');
 const timer = setTimeout(() => {
     handleSuccessHide1('d-none')
   }, 3000)
   handleInputUpdate({name:'',id:''});
})

}
 
// For Edit Data //
const editData=(id)=>{
    handleInput({name:'',phone:'',email:'',city:'',address:''});  
axios.get('http://localhost:3010/company/edit',
        {params: { id: id  },
}).then((res)=>{
     
    handleInput(res.data);
  
})
}


//For Delete Data///
const setdelete=(id)=>{
changedeleteid(id);
}
const deleteData=()=>{
   
    axios.get('http://localhost:3010/designation/delete',{params:{
        id:deleteid
    }}).then((res)=>{
        closeDelete.current.click();
        handleSuccessHide1('');
        const timer = setTimeout(() => {
            handleSuccessHide1('d-none')
          }, 3000)
    })

}

let tabledata;



    return (
        <Fragment>
<div className="content">
                <div className="container-fluid">
                    
                      
                        <div className="row">
                        <div className="col-md-12">
                            <div className="card">
                                <div className="card-header">
                                    <h4 className="card-title">View Company</h4>
                                </div>
                                <div className="card-body">
                                <div className="row">
                           
                           <div className="col-md-12 ">
                           <div className={'alert alert-success '+successhide1} >
                           <button type="button" aria-hidden="true" class="close" data-dismiss="alert">
                               <i class="nc-icon nc-simple-remove"></i>
                           </button>
                           <span>
                               <b> Data Updated Successfully</b></span>
                       </div>
                   
                       </div></div>
                                        <div className="row">
                                            <div className="col-lg-12 ">
                                            <div className="table-responsive">
                                            <table id="example1" class="table table-bordered table-striped table-sm " >
                    <thead>
                                        <tr>
                                        <th  scope="col"  >Sno</th>
                                        <th scope="col"  >Type</th>
                                        <th scope="col"  >Designation</th>
                                        <th scope="col"  >Name</th>
                                        <th scope="col"  >Email</th>
                                        <th scope="col"  >Phone</th>
                                        <th scope="col"  >City</th>
                                        <th scope="col"  >Address</th>
                                    
                                          
                                            <th scope="col"   >Action</th>
                                            </tr>
                                        </thead>
                                        <tbody>
                   { data1.map((list,index)=>{
       
  
    return (
        <Fragment>
        <tr scope="row" key={index}>

        <td >{++index}</td>
        <td  >{list.type}</td>
        <td  > </td>
        <td  >{list.name}</td>
        <td  >{list.email}</td>
        <td  >{list.phone}</td>
        <td  >{list.city}</td>
        <td  >{list.address}</td>
        
       
      
        <td  className="border-0"><a href="javascript:;" data-toggle="modal" data-target="#myModal1" onClick={()=>editData(list._id)}  ><i className="fa fa-edit text-info"></i></a> <a href="javascript:;"  onClick={()=>setdelete(list._id)} data-toggle="modal" data-target="#deleteModal" > <i className="fa fa-trash text-danger"></i></a></td>
        </tr>

       {list.staff.length>0?
           list.staff.map((l)=>{
           return ( 
           <tr >
                <td className="p-1"></td>
           <td className="p-1"></td>
                    <td className="p-1" >{l.designation}</td>
                    <td  className="p-1">{l.name}</td>
                    <td  className="p-1">{l.email}</td>
                    <td  className="p-1">{l.phone}</td>
                    <td className="p-1"></td>
                    <td className="p-1"></td>
                    <td className="py-1 border-0" ><a href="javascript:;" data-toggle="modal" data-target="#myModal1" onClick={()=>editData(l.id)}  ><i className="fa fa-edit text-info"></i></a> <a href="javascript:;"  onClick={()=>setdelete(l.id)} data-toggle="modal" data-target="#deleteModal" > <i className="fa fa-trash text-danger"></i></a></td>
    
                </tr>
            );
                }):''
            }</Fragment>
    
    
       )
})
                                         }
                                          
                                        </tbody>
                                    </table>
        
     

</div>
                                            </div>
                                             
                                           
                                        </div>
                                   
                                      
                                         
                                    
                                </div>
                            </div>
                        </div>
                        
                        </div>
                    </div>
                </div>
                <div class='modal fade 'id="myModal1" >
    <div class="modal-dialog modal-dialog-centered modal-lg">
      <div class="modal-content">
      
       
        <div class="modal-header">
          <h4 class="modal-title mt-0">Edit Company</h4>
          <button type="button" class="close" data-dismiss="modal">&times;</button>
        </div>
         
        <div class="modal-body">
       <div className="row">
                                                <div className="col-md-4 ">
                                                <div className="form-group">
                                                        <label>FullName</label>
        <input type="text" id="name"  value={input.name || ''} onChange={handleChange} className={"form-control "+errors.name}   placeholder="Enter FullName" />
        <span className="text-danger"></span>   </div>
                                                    
                                                </div>
                                                <div className="col-md-4 ">
                                                <div className="form-group">
                                                        <label>Phone Number</label>
        <input type="number" id="phone"  value={input.phone || ''} onChange={handleChange}  className={"form-control "+errors.phone}  placeholder="Enter  Phone Number" />
        <span className="text-danger"></span>   </div>
                                                    
                                                </div>

                                                <div className="col-md-4 ">
                                                <div className="form-group">
                                                        <label>Email</label>
        <input type="email" id="email" value={input.email || ''} onChange={handleChange} className={"form-control "+errors.email}  placeholder="Enter Email" />
        <span className="text-danger"></span>   </div>
                                                    
                                                </div>
                                                </div>
                                                <div className="row ">
                                                <div className="col-md-3 ">
                                                <div className="form-group">
                                                        <label>City</label>
        <input type="text" id="city"  value={input.city || ''} onChange={handleChange} className={"form-control "+errors.city}  placeholder="Enter City " />
        <span className="text-danger"></span>   </div>
                                                    
                                                </div>
                                                <div className="col-md-9 ">
                                                <div className="form-group">
                                                        <label>Address</label>
        <textarea type="text" id="address"   value={input.address || ''} onChange={handleChange}   className={"form-control "+errors.address}  placeholder="Enter  Full Address" ></textarea>
        <span className="text-danger"></span>   </div>
                                                    
                                                </div>
                                                
                                            
        </div></div>
        
       
        <div class="modal-footer">
        <button type="button" className="btn btn-secondary" data-dismiss="modal" ref={inputEl}>Close</button>
          <button type="button" className="btn btn-info btn-fill" onClick={()=>handleSubmitUpdate()} >Submit</button>
        </div>
        
      </div>
    </div>
  </div>
       <DeleteModal name="Company" deleteData={()=>deleteData()} closeDelete={closeDelete}/>
        </Fragment>  
    )}

    export default ViewCompany
