import React, { Fragment ,useState, useEffect,useRef} from 'react';
import axios from 'axios';
import Datatable from 'react-bs-datatable';
 
const AddDesignation=()=>{
    
    const header = [
        { title: 'Sno', prop: 'sno', sortable: true,filterable: true },
        { title: 'Name', prop: 'name', sortable: true,filterable: true },
        { title: 'Action', prop: 'action', sortable: true,filterable: true },
      
      ];
     
    const [input,handleInput]=useState({name:''});
    
    const [updatedata,handleInputUpdate]=useState({id:'',name:''});
    let [successhide,handleSuccessHide]=useState('d-none');
    let [errors,handleValidation]=useState({name:''});
    let [data1,showdata]=useState([[{name:'asdasd'}]]);
    const inputEl = useRef(null);
    const handleChange=(e)=>{
      handleInput({
          [e.target.id]:e.target.value
      })
    }
   const handleSubmit=(e)=>{
       
       e.preventDefault();
if(input.name==''){
handleValidation({...errors,name:'Name Required'});
}
else{
    handleValidation({...errors,name:''});
} 
axios.post('http://localhost:3010/designation/add',input).then(res=>{
    handleSuccessHide('');
    const timer = setTimeout(() => {
        handleSuccessHide('d-none')
      }, 3000)
    handleInput({name:''});
})

   }


   const handleSubmitUpdate=(e)=>{
        
if(updatedata.name==''){
handleValidation({...errors,name:'Name Required'});
}
else{
 handleValidation({...errors,name:''});
} 
axios.post('http://localhost:3010/designation/add',input).then(res=>{
 handleSuccessHide('');
 const timer = setTimeout(() => {
     handleSuccessHide('d-none')
   }, 3000)
 handleInput({name:''});
})

}
const updateData=()=>{

}
const editData=(id)=>{
   
   
axios({url:'http://localhost:3010/designation/edit',
method:'get',
        data: { id: id  },
         headers: {
    'crossDomain': true,  //For cors errors 
    
}

}).then((res)=>{
     
    handleInputUpdate({name:res.data.name,id:res.data._id})
})
}
   useEffect(()=>{
    const fetchData = async () => {
       
  const dataget=await axios.get('http://localhost:3010/designation/').then((res)=>{
        
    showdata(res.data.map((list,index)=>{
        return(
  { sno: ++index, name:list.name,action:<div><a href="javascript:;" onClick={()=>editData(list._id)} data-toggle="modal" data-target="#myModal1" >Edit </a><a href="javascript:;"> Delete</a></div>  }
    )
})
  )});
}
fetchData();

},[]);


    return (
        <Fragment>
<div className="content">
                <div className="container-fluid">
                    <div className="row">
                        <div className="col-md-12">
                            <div className="card">
                                <div className="card-header">
                                    <h4 className="card-title">Add Designation</h4>
                                </div>
                                <div className="card-body">
                                    <div className="row">
                           
                                        <div className="col-md-12 ">
                                        <div className={'alert alert-success '+successhide} >
                                        <button type="button" aria-hidden="true" class="close" data-dismiss="alert">
                                            <i class="nc-icon nc-simple-remove"></i>
                                        </button>
                                        <span>
                                            <b> Data Inserted Successfully</b></span>
                                    </div>
                                
                                    </div></div>
                                    <form onSubmit={(e)=>handleSubmit(e)}>

                                        <div className="row">
                            
                                            <div className="col-md-5 ">
                                            <div className="form-group">
                                                    <label>Enter Name</label>
    <input type="text" id="name" value={input.name || ''} onChange={(e)=>handleChange(e)} className="form-control" disabled="" placeholder="Enter Desgination Name" />
                                                </div>
                                                
                                            </div>
      <div className="col-md-2 mt-4">
                                            <button type="submit" className="btn btn-info btn-fill ">Submit</button>
                                            
                                            </div>
                                         
                                        </div>
                                        
                                      
                                    
                                        <div className="clearfix"></div>
                                    </form>
                                </div>
                            </div>
                        </div>
                        
                        </div>
                        <div className="row">
                        <div className="col-md-12">
                            <div className="card">
                                <div className="card-header">
                                    <h4 className="card-title">View Designation</h4>
                                </div>
                                <div className="card-body">
                                   
                                        <div className="row">
                                            <div className="col-lg-12 ">
                                            <div className="table-responsive">
 
            <Datatable tableHeaders={header} tableBody={data1} rowsPerPage={10}
    rowsPerPageOption={[10, 20, 30, 40]} filterable sortavble />;
     

</div>
                                            </div>
                                             
                                           
                                        </div>
                                   
                                      
                                         
                                    
                                </div>
                            </div>
                        </div>
                        
                        </div>
                    </div>
                </div>
                <div class="modal fade" id="myModal1">
    <div class="modal-dialog modal-dialog-centered">
      <div class="modal-content">
      
       
        <div class="modal-header">
          <h4 class="modal-title mt-0">Edit Designation</h4>
          <button type="button" class="close" data-dismiss="modal">&times;</button>
        </div>
         
        <div class="modal-body">
        <div className="col-md-12 ">
                                            <div className="form-group">
                                                    <label>Enter Name</label>
    <input type="text" id="nameupdate" value={updatedata.name || ''} onChange={(e)=>handleInputUpdate(e)} className="form-control" disabled="" placeholder="Enter Desgination Name" />
                                                </div>
                                                
                                            </div>
        </div>
        
       
        <div class="modal-footer">
        <button type="button" class="btn btn-secondary" data-dismiss="modal">Close</button>
          <button type="button" class="btn btn-info btn-fill" onClick={updateData()} data-dismiss="modal">Submit</button>
        </div>
        
      </div>
    </div>
  </div>
         
        </Fragment>
    )}

    export default AddDesignation
